def longestSubstringWithoutRepeatingCharacters(string):
    longest = ""
    current = ""
    for c in string:
        if c not in current:
            current += c
        else:
            if len(current) > len(longest):
                longest = current
            current = c
    if len(current) > len(longest):
        longest = current
    return longest
